-- CREATE DB

CREATE SCHEMA cloudbreak_cuisine;

-- CREATE TABLES
DROP TABLE IF EXISTS cloudbreak_cuisine.clusters;
CREATE TABLE cloudbreak_cuisine.clusters
(
id integer unique NOT NULL PRIMARY KEY,
version VARCHAR(50) NOT NULL,
cluster_type VARCHAR(50) NOT NULL,
ambari_repo_version text,
ambari_base_url text,
ambari_gpg_key_url text,
repo_version text,
repo_def_file_url text
);

DROP TABLE IF EXISTS cloudbreak_cuisine.services;
CREATE TABLE cloudbreak_cuisine.services
(
id integer unique NOT NULL PRIMARY KEY,
service_description VARCHAR(50) NOT NULL,
cluster_id integer NOT NULL,
mandatory integer NOT NULL,
extensible integer NOT NULL,
display integer NOT NULL,
img VARCHAR(200) NOT NULL,
dependency VARCHAR(50)
);

DROP TABLE IF EXISTS cloudbreak_cuisine.recipes;
CREATE TABLE cloudbreak_cuisine.recipes
(
id SERIAL PRIMARY KEY,
serviceid integer,
recipename text,
recipedescription text,
addon_type text,
addon_content text,
recipe_type text,
mandatory integer,
display integer,
content text
);

DROP TABLE IF EXISTS cloudbreak_cuisine.recipes_nodes;
CREATE TABLE cloudbreak_cuisine.recipes_nodes
(
id SERIAL PRIMARY KEY,
recipe_id integer,
node_type text
);

DROP TABLE IF EXISTS cloudbreak_cuisine.services_dependencies;
CREATE TABLE cloudbreak_cuisine.services_dependencies
(
id SERIAL NOT NULL PRIMARY KEY,
service_id integer NOT NULL,
service_description VARCHAR(50) NOT NULL,
requires_service_id integer NOT NULL,
required_service_description VARCHAR(50) NOT NULL
);

DROP TABLE IF EXISTS cloudbreak_cuisine.components_blueprints;
CREATE TABLE cloudbreak_cuisine.components_blueprints
(
id integer unique NOT NULL PRIMARY KEY,
service_id integer NOT NULL,
component_description VARCHAR(50) NOT NULL,
config text,
master_blueprint text,
worker_blueprint text,
compute_blueprint text
);


DROP TABLE IF EXISTS cloudbreak_cuisine.management_packs;
CREATE TABLE cloudbreak_cuisine.management_packs
(
id integer unique NOT NULL PRIMARY KEY,
cluster_id integer NOT NULL,
description VARCHAR(255) NOT NULL,
url text
);


DROP TABLE IF EXISTS cloudbreak_cuisine.bundles;
CREATE TABLE cloudbreak_cuisine.bundles
(
id SERIAL NOT NULL PRIMARY KEY,
name text,
description text,
image text,
type text
);

DROP TABLE IF EXISTS cloudbreak_cuisine.bundles_dependencies;
CREATE TABLE cloudbreak_cuisine.bundles_dependencies
(
id SERIAL NOT NULL PRIMARY KEY,
bundle_id integer,
dep_type text,
dep_id integer
);

DROP TABLE IF EXISTS cloudbreak_cuisine.bundles_contents;
CREATE TABLE cloudbreak_cuisine.bundles_contents
(
id SERIAL NOT NULL PRIMARY KEY,
bundle_id integer,
type text,
content text
);

DROP TABLE IF EXISTS cloudbreak_cuisine.whoville_bundles;
CREATE TABLE cloudbreak_cuisine.whoville_bundles
(
id integer NOT NULL PRIMARY KEY,
name text UNIQUE NOT NULL,
description text,
image text,
type text
);


DROP TABLE IF EXISTS cloudbreak_cuisine.whoville_bundles_dependencies;
CREATE TABLE cloudbreak_cuisine.whoville_bundles_dependencies
(
id integer NOT NULL PRIMARY KEY,
bundle_id integer,
dep_type text,
