);


DROP TABLE IF EXISTS cloudbreak_cuisine.whoville_bundles_contents;
CREATE TABLE cloudbreak_cuisine.whoville_bundles_contents
(
id integer NOT NULL PRIMARY KEY,
bundle_id integer,
type text,
content text
);


DROP TABLE IF EXISTS cloudbreak_cuisine.whoville;
CREATE TABLE cloudbreak_cuisine.whoville
(
id SERIAL NOT NULL PRIMARY KEY,
name text,
version text,
description text,
image text,
download_url text
);

DROP TABLE IF EXISTS cloudbreak_cuisine.users;
CREATE TABLE cloudbreak_cuisine.users
(
id SERIAL PRIMARY KEY,
name text,
username text,
password text,
email text,
role text,
img text
);


DROP TABLE IF EXISTS cloudbreak_cuisine.profiles;
CREATE TABLE cloudbreak_cuisine.profiles
(
id SERIAL PRIMARY KEY,
name text,
user_name text,
profile_type text,
