);
DROP TABLE IF EXISTS cloudbreak_cuisine.whoville_profile_credentials;
CREATE TABLE cloudbreak_cuisine.whoville_profile_credentials
(
id integer PRIMARY KEY,
whoville_profile_id integer,
type text,
type_img text,
provider text,
region text,
bucket text,
